import formAutoContent from 'form-auto-content'
import { createReadStream } from 'fs'
import { afterEach, beforeEach, describe, expect, test, vi } from 'vitest'

import { buildVariantPath } from '@rfcx-bio/common/api-bio/_helpers'
import { modelRepositoryWithElevatedPermissions } from '@rfcx-bio/testing/dao'
import { makeApp } from '@rfcx-bio/testing/handlers'

import { USER_CONFIG } from '@/users/user-profile-bll'
import { PATCH } from '~/api-helpers/types'
import { getMetadata } from '~/image'
import { getObject } from '~/storage'
import { routesUserProfile } from './index'

vi.mock('../_services/api-core/api-core', () => {
  return {
    patchUserProfileOnCore: vi.fn(async () => {
      await Promise.resolve()
    })
  }
})

const ROUTE = '/profile-image'
const token = 'Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6IlF6UTROakpHTkRVd01UTTJSVGREUmpRMVFqazROa001UVVFMU1URTJSREl5T1VJME9VRkVOQSJ9..O2iT62WQuRi5x-Og3fI9IQbHsySojWbKnsjV4-FQfISls-dxkavpTNPm9SnBtNqkuW5lVJsAXVTEZkwjDucFMHwOYqKCcVLSDLqo319NMlXLac1SKsjR1uhDNGPTziDypxoCzMtVIOiENR1PUYu7leNPzg1uIB0LyMFDqVElUgKAzUW9cCE3fi6Ir9AknjJCbuL5UsWFSLjctKfiwo0-hnG6NEjPQZzrEpbePi5sn9O1vXljgBwYnkjXE9OF6GJpxw3zNmcYiTUqv-z0OdPBExLcNZ3uGWImSTD_KV3aT2w0VYINCotx5xHUynPDAeuOTffl6X5USQR14Olcyu2X5A'

const existingImage = 'xyz.png'
const localImageUrl = '../website/src/_assets/default-species-image.jpg'
const defaultUserToken = {
  email: 'lucy@rfcx.org',
  idAuth0: 'auth0|65dc9f09a5efc15b78525a84',
  firstName: 'Lucy',
  lastName: 'D'
}

const defaultUserProfile = { id: 1, image: existingImage, ...defaultUserToken }

const { UserProfile } = modelRepositoryWithElevatedPermissions

beforeEach(async () => {
  await UserProfile.create(defaultUserProfile)
})

afterEach(async () => {
  await UserProfile.destroy({ where: { id: defaultUserProfile.id } })
})

describe('PATCH /profile-image', async () => {
  test('updates profile image', async () => {
    // Arrange
    const app = await makeApp(routesUserProfile, { userId: defaultUserProfile.id, userToken: defaultUserToken })
    const form = formAutoContent({
      file: createReadStream(localImageUrl)
    })
    form.headers.authorization = token

    // Act
    const response = await app.inject({
      method: PATCH,
      url: ROUTE,
      ...form
    })

    // Assert
    expect(response.statusCode).toBe(204)
    const profile = await UserProfile.findOne({ where: { email: defaultUserProfile.email } })
    expect(profile?.image).not.toBe(existingImage)
    const fileAsArrayBuffer = await getObject(profile?.image ?? '')
    expect(fileAsArrayBuffer.byteLength).toBeGreaterThan(1000)
  })

  test('generates and saves a thumbnail image', async () => {
    // Arrange
    const app = await makeApp(routesUserProfile, { userId: defaultUserProfile.id, userToken: defaultUserToken })
    const form = formAutoContent({
      file: createReadStream(localImageUrl)
    })
    form.headers.authorization = token

    // Act
    const response = await app.inject({
      method: PATCH,
      url: ROUTE,
      ...form
    })

    // Assert
    expect(response.statusCode).toBe(204)
    const profile = await UserProfile.findOne({ where: { email: defaultUserProfile.email } })
    const originalImage = profile?.image ?? ''
    const thumbnailImage = buildVariantPath(originalImage, 'thumbnail')
    const fileAsArrayBuffer = await getObject(thumbnailImage)
    expect(fileAsArrayBuffer).toBeDefined()
  })

  test.skip('thumbnail image has correct dimensions', async () => {
    // Arrange
    const app = await makeApp(routesUserProfile, { userId: defaultUserProfile.id, userToken: defaultUserToken })
    const form = formAutoContent({
      file: createReadStream(localImageUrl)
    })
    form.headers.authorization = token

    // Act
    const response = await app.inject({
      method: PATCH,
      url: ROUTE,
      ...form
    })

    // Assert
    expect(response.statusCode).toBe(204)
    const profile = await UserProfile.findOne({ where: { email: defaultUserProfile.email } })
    const originalImage = profile?.image ?? ''
    const thumbnailImage = buildVariantPath(originalImage, 'thumbnail')
    const fileAsArrayBuffer = await getObject(thumbnailImage)
    expect(fileAsArrayBuffer).toBeDefined()
    const imageMetadata = await getMetadata(fileAsArrayBuffer)
    const config = USER_CONFIG.image.thumbnail
    expect(imageMetadata?.width).toEqual(config.width)
    expect(imageMetadata?.height).toEqual(config.height)
  })
})
